import React, { useState } from "react";
import "./Test.scss";
function Detail(props) {
  // const [products] = useState([
  //   {
  //     _id: 1,
  //     title: "Nike Shoes",
  //     description: "Mẫu Giày Thể Thao Nam Phong Cách Trẻ Trung",
  //     content:
  //       "Dành cho các bạn nam, đủ mọi loại size, thoáng mát, dễ chịu, bền, đẹp.",
  //     price: 23,
  //     colors: ["red", "black", "crimson", "teal"],
  //     count: 1,
  //     index: 0,
  //   },
  // ]);

  const data = [
    {
      id: 1,
      img: "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
    },
    {
      id: 2,
      img: "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
    },
    {
      id: 3,
      img: "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
    },
    {
      id: 4,
      img: "https://www.upsieutoc.com/images/2020/06/27/img4.jpg",
    },
  ];
  const [bigImg, setBigImg] = useState(data[0].img);
  console.log(data);
  return (
    <div className="app">
      <img src={bigImg} alt="" width="500px" height="500px" className="img" />
      <br />
      {data.map((item) => {
        return (
          <div key={item.id} width="100%" height="100%" className="">
            {item.img}
          </div>
        );
      })}

      {data.map((element) => (
        <div key={element.id}>
          <img src={element.img} alt="" />
        </div>
      ))}
    </div>
  );
}

export default Detail;
