import React, { useEffect, useState } from "react";
import "./Detail.scss";

class Detail extends React.Component {
  state = {
    products: [
      {
        _id: 1,
        title: "Nike Shoes",
        src: [
          "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
          "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
          "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
          "https://www.upsieutoc.com/images/2020/06/27/img4.jpg",
        ],
        description: "UI/UX designing, html css tutorials",
        content:
          "Welcome to our chanel Dev AT. Here you learn web designing, UI/UX designing, html css tutorials, css animations, javascript and jquery",
        price: 23,
        colors: ["red", "black", "crimson", "teal"],
        count: 1,
      },
    ],
    index: 0,
  };
  handleTab = (index) => {
    this.setState({ index: index });
  };
  render() {
    const { products, index } = this.state;
    return (
      <div className="app">
        {products.map((item) => (
          <div className="details" key={item._id}>
            <div className="big-img">
              <img src={item.src[index]} alt="" />
            </div>

            <div className="box">
              <div className="row">
                <h2>{item.title}</h2>
                <span>${item.price}</span>
              </div>

              <div className="colors">
                {item.colors.map((color, index) => (
                  <button style={{ background: color }} key={index}></button>
                ))}
              </div>

              <p>{item.description}</p>
              <p>{item.content}</p>

              <div className="thumb">
                {item.src.map((img, index, i) => (
                  <img
                    src={img}
                    alt=""
                    key={index}
                    onClick={() => this.handleTab(index)}
                  />
                ))}
              </div>
              <button className="cart">Add to cart</button>
            </div>
          </div>
        ))}
      </div>
    );
  }
}
export default Detail;
