import React, { useState } from "react";
import "./Detail.scss";

function Detail(props) {
  const { products } = useState({
    _id: 1,
    title: "Nike Shoes",
    src: [
      "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
      "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
      "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
      "https://www.upsieutoc.com/images/2020/06/27/img4.jpg",
    ],
    description: "UI/UX designing, html css tutorials",
    content:
      "Welcome to our chanel Dev AT. Here you learn web designing, UI/UX designing, html css tutorials, css animations, javascript and jquery",
    price: 23,
    colors: ["red", "black", "crimson", "teal"],
    count: 1,
    index: 0,
  });

  var myRef = React.createRef();

  function handleTab(index) {
    products.index = index;
    const images = myRef.current.children;
    for (let i = 0; i < images.length; i++) {
      images[i].className = images[i].className.replace("active", "");
    }
    images[index].className = "active";
  }

  return (
    <div className="app">
      {
        <div className="details" key={products._id}>
          <div className="big-img">
            <img src={products.src[products.index]} alt="" />
          </div>

          <div className="box">
            <div className="row">
              <h2>${products.title}</h2>
              <span>${products.price}</span>
            </div>

            <div className="colors">
              {products.colors.map((color, index) => (
                <button style={{ background: color }} key={index}></button>
              ))}
            </div>

            <p>{products.description}</p>
            <p>{products.content}</p>

            <div className="thumb" ref={myRef}>
              {products.src.map((img, index) => (
                <img
                  src={img}
                  alt=""
                  key={index}
                  onClick={() => handleTab(index)}
                />
              ))}
            </div>
            <button className="cart">Add to cart</button>
          </div>
        </div>
      }
    </div>
  );
}

export default Detail;
