import React, { useState } from "react";
import "./Test.scss";
function Detail(props) {
  const [products] = useState([
    {
      _id: 1,
      title: "Nike Shoes",
      description: "Mẫu Giày Thể Thao Nam Phong Cách Trẻ Trung",
      content:
        "Dành cho các bạn nam, đủ mọi loại size, thoáng mát, dễ chịu, bền, đẹp.",
      price: 23,
      colors: ["red", "black", "crimson", "teal"],
      count: 1,
      index: 0,
    },
  ]);

  const [src] = useState([
    "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
    "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
    "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
    "https://www.upsieutoc.com/images/2020/06/27/img4.jpg",
  ]);
  const [bigImg, setBigImg] = useState(src[0]);

  return (
    <div className="app">
      <img src={bigImg} alt="" width="500px" height="500px" className="img" />
      <br />

      <img
        src={src[0]}
        alt=""
        width="300px"
        height="300px"
        className="img"
        onClick={() => setBigImg(src[0])}
      />
      <img
        src={src[1]}
        alt=""
        width="300px"
        height="300px"
        className="img"
        onClick={() => setBigImg(src[1])}
      />
      <img
        src={src[2]}
        alt=""
        width="300px"
        height="300px"
        className="img"
        onClick={() => setBigImg(src[2])}
      />
      <img
        src={src[3]}
        alt=""
        width="300px"
        height="300px"
        className="img"
        onClick={() => setBigImg(src[3])}
      />
    </div>
  );
}

export default Detail;
