const data = [
  {
    id: 1,
    title: "Quần áo",
    img: 'https://sites.google.com/site/hinhanhdep24h/_/rsrc/1436687439788/home/hinh%20anh%20thien%20nhien%20dep%202015%20%281%29.jpeg',
    price: 100
