    price: 300
  },
  {
    id: 4,
    title: "Máy bay",
    img: 'https://hanoimoi.com.vn/Uploads/tuandiep/2018/4/8/1(1).jpg',
    price: 40
  },

]
function App() {

  const [list, setList] = useState([])
  //call api xông. thì setList([ đât là dữ liệu từ api ])

  useEffect(() => {
    console.log("chạy vào đây ")
  }, [])
  return (
    <div className="App">

      <Header />

      <div className="main">
        <Row gutter={[16, 16]}>
          {
            list && list.map(element => (
              <Col span={6}>
                <Product data={element} />
              </Col>
            ))
          }
        </Row>
      </div>

    </div>
  );
}

export default App;


